import React,  {useState} from 'react'

export default function Textbox(props) {

  const handleONclicku = ()=>{
    // console.log('button is click' + text);
    let nowtext = text.toLocaleUpperCase()
    setText(nowtext)
  }

  const handleONclickl = ()=>{
    // console.log('button is click' + text);
    let nowtext = text.toLocaleLowerCase()
    setText(nowtext)
  }

  const handleONclickd = ()=>{
    setText('')
  }


  
  const handleONchange = (event)=>{
    // console.log('on change');
    setText(event.target.value)
    // console.log(event.target.value);
  }

  //copytxt

  const copytxt = ()=>{
    var textq = document.getElementById('mybox');
    textq.select();
    navigator.clipboard.writeText(textq.value)
  }

  //remove sextra sace
  
 const space = ()=>{
    let newtxt = text.split(/[ ]+/);
    setText(newtxt.join(" "))

 }


//dark mode


  const bgcolorchange = ()=>{
    if(bgcolor.color === 'black'){
     setbgcolor({
      color: 'white',
      backgroundColor: 'black',

     })
     setbtntxt('Light mode')
    }
    else{
      setbgcolor({
        color: 'black',
        backgroundColor: 'white'
       })
       setbtntxt('Dark mode')
    }
  }

  const [bgcolor , setbgcolor] = useState({
    color: 'black',
    backgroundColor: 'white'
  })

  const [btntxt , setbtntxt] = useState('Dark mode')

  
// dark mode finish


  const [text , setText] = useState('')
  return (
    <div style={bgcolor}>
        <h1>{props.title} </h1>  
         <div class="form-check form-switch">
  <input class="form-check-input" onClick={bgcolorchange} type="checkbox" role="switch" id="flexSwitchCheckDefault"/>
  <label class="form-check-label" for="flexSwitchCheckDefault">{btntxt}</label>
</div>
        <div className="mb-3" style={bgcolor}>
             <textarea className="form-control" placeholder='type here' value={text} onChange= {handleONchange}id="mybox" rows="9"></textarea>
        </div>
              <button className='btn btn-primary mx-1 my-1' onClick={handleONclicku}>Convert to Uppercase</button>
              <button className='btn btn-primary mx-1 my-1' onClick={handleONclickl}>Convert to Lowercase</button>

               <button className='btn btn-danger mx-1 my-1'  onClick={handleONclickd}>Clear text</button>
               <button className='btn btn-primary mx-1 my-1'  onClick={copytxt}>Copy</button>
               <button className='btn btn-primary mx-1 my-1'  onClick={space}>Remove extra space</button>
              
             




              <div className='containar my-2'>
             
                <h2>Your text summary</h2>
                <p>{text.split(" ").length} Words and {text.length} characters</p>
              </div>
    </div>   
  )
}
